  --shadow-md: 0px 6px 12px -2px hsl(210 10% 2% / 0.35), 0px 3px 6px -3px hsl(210 10% 2% / 0.25);
  --shadow-lg: 0px 10px 20px -4px hsl(210 10% 2% / 0.40), 0px 4px 8px -4px hsl(210 10% 2% / 0.30);
  --shadow-xl: 0px 20px 30px -6px hsl(210 10% 2% / 0.45), 0px 8px 12px -6px hsl(210 10% 2% / 0.35);
  --shadow-2xl: 0px 25px 50px -12px hsl(210 10% 2% / 0.50);

}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/**
 * Using the elevate system.
 * Automatic contrast adjustment.
 *
 * <element className="hover-elevate" />
 * <element className="active-elevate-2" />
 *
 * // Using the tailwind utility when a data attribute is "on"
 * <element className="toggle-elevate data-[state=on]:toggle-elevated" />
 * // Or manually controlling the toggle state
 * <element className="toggle-elevate toggle-elevated" />
 *
 * Elevation systems have to handle many states.
 * - not-hovered, vs. hovered vs. active  (three mutually exclusive states)
 * - toggled or not
 * - focused or not (this is not handled with these utilities)
 *
 * Even without handling focused or not, this is six possible combinations that
 * need to be distinguished from eachother visually.
 */
@layer utilities {

  /* Primary button special effects */
  button.bg-primary,
  a.bg-primary {
    position: relative;
    overflow: hidden;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    background: linear-gradient(135deg, hsl(180, 50%, 28%) 0%, hsl(180, 50%, 32%) 100%);
    font-weight: 600;
    letter-spacing: 0.3px;
  }

  button.bg-primary::before,
  a.bg-primary::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    border-radius: 50%;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.3) 0%, rgba(255, 255, 255, 0.1) 100%);
    transform: translate(-50%, -50%);
    transition: width 0.6s ease, height 0.6s ease;
    z-index: 0;
  }

  button.bg-primary::after,
  a.bg-primary::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(135deg, transparent 0%, rgba(255, 255, 255, 0.05) 100%);
    z-index: 0;
  }

  button.bg-primary:hover::before,
  a.bg-primary:hover::before {
    width: 350px;
    height: 350px;
  }

  button.bg-primary:hover,
  a.bg-primary:hover {
    transform: translateY(-3px);
    background: linear-gradient(135deg, hsl(180, 50%, 30%) 0%, hsl(180, 50%, 34%) 100%);
  }

  button.bg-primary:active,
  a.bg-primary:active {
    transform: translateY(-1px);
  }

  button.bg-primary > *,
  a.bg-primary > * {
    position: relative;
    z-index: 1;
  }

  /* Outline button special effects */
  button:has(.border):not(.bg-primary),
  a:has(.border):not(.bg-primary) {
    position: relative;
    overflow: hidden;
    font-weight: 600;
    letter-spacing: 0.3px;
  }

  /* Specifically target outline variant buttons */
  button[class*="border"][class*="button-outline"],
  .border.shadow-xs {
    position: relative;
    overflow: hidden;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    backdrop-filter: blur(8px);
    background: rgba(255, 255, 255, 0.9);
    border: none;
    font-weight: 600;
    letter-spacing: 0.3px;
  }

  .border.shadow-xs::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    border-radius: 50%;
    background: radial-gradient(circle, rgba(0, 0, 0, 0.08) 0%, rgba(0, 0, 0, 0.02) 100%);
    transform: translate(-50%, -50%);
    transition: width 0.6s ease, height 0.6s ease;
    z-index: 0;
  }

  .border.shadow-xs:hover::before {
    width: 350px;
    height: 350px;
  }

  .border.shadow-xs:hover {
    transform: translateY(-3px);
    background: rgba(255, 255, 255, 0.95);
  }

  .border.shadow-xs:active {
    transform: translateY(-1px);
  }

  .border.shadow-xs > * {
    position: relative;
    z-index: 1;
  }

  /* Hide ugly search cancel button in Chrome until we can style it properly */
  input[type="search"]::-webkit-search-cancel-button {
    @apply hidden;
  }

  /* Placeholder styling for contentEditable div */
  [contenteditable][data-placeholder]:empty::before {
    content: attr(data-placeholder);
    color: hsl(var(--muted-foreground));
    pointer-events: none;
  }

  /* .no-default-hover-elevate/no-default-active-elevate is an escape hatch so consumers of
   * buttons/badges can remove the automatic brightness adjustment on interactions
   * and program their own. */
  .no-default-hover-elevate {}

  .no-default-active-elevate {}


  /**
   * Toggleable backgrounds go behind the content. Hoverable/active goes on top.
   * This way they can stack/compound. Both will overlap the parent's borders!
   * So borders will be automatically adjusted both on toggle, and hover/active,
   * and they will be compounded.
   */
  .toggle-elevate::before,
  .toggle-elevate-2::before {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: -1;
    /* sits behind content but above backdrop */
  }

  .toggle-elevate.toggle-elevated::before {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.toggle-elevate::before {
    inset: -1px;
  }

  /* Does not work on elements with overflow:hidden! */
  .hover-elevate:not(.no-default-hover-elevate),
  .active-elevate:not(.no-default-active-elevate),
  .hover-elevate-2:not(.no-default-hover-elevate),
  .active-elevate-2:not(.no-default-active-elevate) {
    position: relative;
    z-index: 0;
  }

  .hover-elevate:not(.no-default-hover-elevate)::after,
  .active-elevate:not(.no-default-active-elevate)::after,
  .hover-elevate-2:not(.no-default-hover-elevate)::after,
  .active-elevate-2:not(.no-default-active-elevate)::after {
    content: "";
    pointer-events: none;
    position: absolute;
    inset: 0px;
    /*border-radius: inherit;   match rounded corners */
    border-radius: inherit;
    z-index: 999;
    /* sits in front of content */
  }

  .hover-elevate:hover:not(.no-default-hover-elevate)::after,
  .active-elevate:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-1);
  }

  .hover-elevate-2:hover:not(.no-default-hover-elevate)::after,
  .active-elevate-2:active:not(.no-default-active-elevate)::after {
    background-color: var(--elevate-2);
  }

  /* If there's a 1px border, adjust the inset so that it covers that parent's border */
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate-2:not(.no-hover-interaction-elevate)::after,
  .border.active-elevate-2:not(.no-active-interaction-elevate)::after,
  .border.hover-elevate:not(.no-hover-interaction-elevate)::after {
    inset: -1px;
  }
}

/* Modern Animated Background Styles */

/* Gradient Blobs */
.blob {
  position: absolute;
  border-radius: 50%;
  filter: blur(60px);
  opacity: 0.15;
  animation: blobFloat 25s ease-in-out infinite;
}

.blob-1 {
  width: 400px;
  height: 400px;
  top: 10%;
  left: -10%;
  background: linear-gradient(135deg, hsl(var(--primary)), hsl(var(--primary) / 0.6));
  animation-delay: 0s;
}

.blob-2 {
  width: 350px;
  height: 350px;
  top: 50%;
  right: -5%;
  background: linear-gradient(225deg, hsl(var(--primary)), hsl(var(--primary) / 0.4));
  animation-delay: -8s;
}

.blob-3 {
  width: 300px;
  height: 300px;
  bottom: 10%;
  left: 30%;
  background: linear-gradient(315deg, hsl(var(--primary)), hsl(var(--primary) / 0.5));
  animation-delay: -15s;
}

/* Floating Letters with Glassmorphism */
.floating-letter {
  position: absolute;
  font-size: clamp(3rem, 6vw, 6rem);
  font-weight: 700;
  color: hsl(var(--primary) / 0.08);
  font-family: var(--font-sans);
  backdrop-filter: blur(2px);
  animation: letterFloat 20s ease-in-out infinite;
  user-select: none;
  text-shadow: 0 0 20px hsl(var(--primary) / 0.1);
}

/* Floating Words */
.floating-word {
  position: absolute;
  font-size: clamp(0.75rem, 1.5vw, 1rem);
  font-weight: 600;
  color: hsl(var(--primary) / 0.15);
  font-family: var(--font-sans);
  letter-spacing: 0.3em;
  backdrop-filter: blur(2px);
  animation: wordFloat 25s ease-in-out infinite;
  user-select: none;
  text-shadow: 0 0 15px hsl(var(--primary) / 0.08);
}

/* Wave Lines */
.wave-line {
  position: absolute;
  width: 100%;
  height: 100%;
  pointer-events: none;
}

.wave-1 {
  top: 20%;
  left: 0;
  animation: waveMove 30s ease-in-out infinite;
}

.wave-2 {
  bottom: 10%;
  right: 0;
  animation: waveMove 35s ease-in-out infinite reverse;
}

/* Floating Circles */
.floating-circle {
  position: absolute;
  width: clamp(40px, 8vw, 100px);
  height: clamp(40px, 8vw, 100px);
  border-radius: 50%;
  background: radial-gradient(circle at 30% 30%, hsl(var(--primary) / 0.12), hsl(var(--primary) / 0.03));
  border: 1px solid hsl(var(--primary) / 0.15);
  backdrop-filter: blur(10px);
  box-shadow: 0 8px 32px hsl(var(--primary) / 0.08);
  animation: circleFloat 25s ease-in-out infinite;
}

/* Animations */
@keyframes blobFloat {
  0%, 100% {
    transform: translate(0, 0) scale(1);
  }
  33% {
    transform: translate(50px, -50px) scale(1.1);
  }
  66% {
    transform: translate(-30px, 30px) scale(0.9);
  }
}

@keyframes letterFloat {
  0%, 100% {
    transform: translateY(0) rotate(0deg);
    opacity: 0.08;
  }
  25% {
    transform: translateY(-30px) rotate(2deg);
    opacity: 0.12;
  }
  50% {
    transform: translateY(-50px) rotate(-2deg);
    opacity: 0.06;
  }
  75% {
    transform: translateY(-30px) rotate(1deg);
    opacity: 0.10;
  }
}

@keyframes wordFloat {
  0%, 100% {
    transform: translateY(0) translateX(0);
    opacity: 0.15;
  }
  50% {
    transform: translateY(-40px) translateX(20px);
    opacity: 0.25;
  }
}

@keyframes waveMove {
  0%, 100% {
    transform: translateX(0);
  }
  50% {
    transform: translateX(-100px);
  }
}

@keyframes circleFloat {
  0%, 100% {
    transform: translate(0, 0) scale(1);
    opacity: 0.5;
  }
  33% {
    transform: translate(30px, -40px) scale(1.15);
    opacity: 0.7;
  }
  66% {
    transform: translate(-20px, 20px) scale(0.9);
    opacity: 0.4;
  }
}

/* Dark mode adjustments */
.dark .blob {
  opacity: 0.08;
}

.dark .floating-letter {
  color: hsl(var(--primary) / 0.12);
  text-shadow: 0 0 30px hsl(var(--primary) / 0.15);
}

.dark .floating-word {
  color: hsl(var(--primary) / 0.2);
  text-shadow: 0 0 20px hsl(var(--primary) / 0.12);
}

.dark .floating-circle {
  background: radial-gradient(circle at 30% 30%, hsl(var(--primary) / 0.15), hsl(var(--primary) / 0.05));
  border-color: hsl(var(--primary) / 0.2);
  box-shadow: 0 8px 32px hsl(var(--primary) / 0.12);
}

/* Letter Popup Animation */
@keyframes popup {
  0% {
    transform: translateY(0) scale(1);
  }
  50% {
    transform: translateY(-10px) scale(1.2);
  }
  100% {
    transform: translateY(0) scale(1);
  }
}

.animate-popup {
  animation: popup 0.6s ease-in-out;
  animation-fill-mode: both;
}

