  targetAudience?: string[];
  curriculum?: string[];
  features?: string[];
  level?: string;
  specialNote?: string;
}

export default function CourseCard({ 
  title, 
  subtitle,
  icon: Icon, 
  schedule,
  targetAudience,
  curriculum,
  features,
  level,
  specialNote
}: CourseCardProps) {
  return (
    <Card className="hover-elevate transition-all duration-300 h-full" data-testid={`card-course-${title}`}>
